<script module lang="ts">
	export interface TeePose {
		bodyRotation: number;
		eyesRotation: number;
		frontFootRotation: number;
		backFootRotation: number;
		eyesPosition: string;
		frontFootPosition: string;
		backFootPosition: string;
	}
</script>

<script lang="ts">
	import { encodeBase64 } from '$lib/base64';
	import { skinQueue } from '$lib/skin-queue';
	import { onDestroy, onMount } from 'svelte';
	import { ddnetColorToRgb } from '$lib/ddnet/helpers';
	import { rgbToFilter } from '$lib/rgbToFilter';
	import { getSkinUrl } from '$lib/stores/skins';

	const {
		/** Target url of the skin, if not provided, either `name` or `url` must be provided */
		url = '',
		/** Target name of the skin, if not provided, either `name` or `url` must be provided */
		name = '',
		/** The body color of the skin, null means no custom body color */
		body = null as number | null,
		/** The feet color of the skin, null means no custom feet color */
		feet = null as number | null,
		/** Whether to use the default skin, otherwise `x_spec` will be used by default */
		useDefault = false,
		/** extra classes */
		className = '',
		/** Whether to always start fetching the skin even if it is not visible */
		alwaysFetch = false,
		/**
		 * Color matching iteration count, default to 3.
		 * This is because we use a weird color to filter converter using a guessimation algorithm.
		 * The color can randomly mismatch. Increasing the iteration can increase the chance of it looking correct.
		 *
		 * We did this because when showing multiple tees, multiple canvases can slow down the browser.
		 * This way we can at least use pure css to render the tees, and it is easier to scale.
		 */
		maxLossIter = 3,
		pose = null as TeePose | null,
		...rest
	} = $props();

	const DEFAULT_SKIN =
		'data:image/png;base64,';
	const X_SPEC_SKIN =
		'data:image/png;base64,';

	let skin = $state(X_SPEC_SKIN);
	let loadingSkin = $state(null) as string | null;
	let bodyFilter = $state(null) as string | null;
	let feetFilter = $state(null) as string | null;

	let abortController: AbortController | null = null;
	let intersectionObserver: IntersectionObserver | null = null;

	let root = $state(null) as Element | null;

	const updateSkin = async () => {
		const thisLoadingSkin = url || name;
		const thisUrl = url;
		const thisName = name;

		if (thisLoadingSkin == loadingSkin) {
			return;
		}

		if (abortController) {
			abortController.abort();
			abortController = null;
		}

		loadingSkin = thisLoadingSkin;
		skin = X_SPEC_SKIN;

		if (!thisName && !thisUrl) {
			skin = useDefault ? DEFAULT_SKIN : X_SPEC_SKIN;
			loadingSkin = 'default';
			return;
		}

		const controller = new AbortController();
		abortController = controller;

		const response = await skinQueue.push(async () => {
			const targetUrl = thisUrl || (await getSkinUrl(thisName));
			if (!targetUrl) return null;

			try {
				return await fetch(targetUrl, { signal: controller.signal });
			} catch {
				return null;
			} finally {
				abortController = null;
			}
		});

		if (response && response.ok) {
			const contentType = response.headers.get('content-type') || '';
			if (contentType.startsWith('image/')) {
				const skinData = await response.arrayBuffer();
				const buffer = new Uint8Array(skinData);
				skin = `data:${contentType};base64,${encodeBase64(buffer)}`;
			} else {
				skin = useDefault ? DEFAULT_SKIN : X_SPEC_SKIN;
				loadingSkin = 'default';
			}
		} else {
			skin = useDefault ? DEFAULT_SKIN : X_SPEC_SKIN;
			loadingSkin = 'default';
		}
	};

	onDestroy(() => {
		if (abortController) {
			abortController.abort();
			abortController = null;
		}

		if (intersectionObserver && root) {
			intersectionObserver.disconnect();
			intersectionObserver = null;
		}
	});

	onMount(async () => {
		if (alwaysFetch) {
			updateSkin();
		}

		intersectionObserver = new IntersectionObserver((entries) => {
			for (const entry of entries) {
				if (entry.isIntersecting) {
					updateSkin();
				} else {
					if (abortController && !alwaysFetch) {
						abortController.abort();
						abortController = null;
					}
				}
			}
		});

		if (root) {
			intersectionObserver.observe(root);
		}
	});

	$effect(() => {
		bodyFilter = body != null ? rgbToFilter(ddnetColorToRgb(body), maxLossIter).color : null;
		feetFilter = feet != null ? rgbToFilter(ddnetColorToRgb(feet), maxLossIter).color : null;
	});

	$effect(() => {
		name;
		requestAnimationFrame(updateSkin);
	});
</script>

<div
	bind:this={root}
	class="tee-render {className}"
	{...rest}
	style="background-image: url({skin}); {pose ? `transform: rotate(${pose.bodyRotation}deg)` : ''}"
>
	{#if bodyFilter && feetFilter}
		<div class="tee-render-pass" style="filter: grayscale(1);">
			<div
				class="tee-foot-outline back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {feetFilter};"
		>
			<div
				class="tee-foot-outline back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
		</div>
		<div class="tee-render-pass" style="filter: grayscale(1);">
			<div class="tee-body-outline"></div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {bodyFilter};"
		>
			<div class="tee-body-outline"></div>
		</div>
		<div class="tee-render-pass" style="filter: grayscale(1);">
			<div
				class="tee-foot-outline front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {feetFilter};"
		>
			<div
				class="tee-foot-outline front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
		</div>

		<div class="tee-render-pass" style="filter: grayscale(1);">
			<div
				class="tee-foot back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {feetFilter};"
		>
			<div
				class="tee-foot back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
		</div>

		<div class="tee-render-pass" style="filter: grayscale(1)">
			<div class="tee-body"></div>
			<div
				class="tee-eyes"
				style={pose
					? `transform: translate(${pose.eyesPosition}) rotate(${pose.eyesRotation}deg)`
					: ''}
			>
				<div class="tee-eye-left"></div>
				<div class="tee-eye-right"></div>
			</div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {bodyFilter}"
		>
			<div class="tee-body"></div>
			<div
				class="tee-eyes"
				style={pose
					? `transform: translate(${pose.eyesPosition}) rotate(${pose.eyesRotation}deg)`
					: ''}
			>
				<div class="tee-eye-left"></div>
				<div class="tee-eye-right"></div>
			</div>
		</div>

		<div class="tee-render-pass" style="filter: grayscale(1);">
			<div
				class="tee-foot front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
		</div>
		<div
			class="tee-render-pass"
			style="mix-blend-mode: multiply; filter: brightness(0) saturate(100%) {feetFilter};"
		>
			<div
				class="tee-foot front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
		</div>
	{:else}
		<div class="tee-render-pass">
			<div
				class="tee-foot-outline back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
			<div class="tee-body-outline"></div>
			<div
				class="tee-foot-outline front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
			<div
				class="tee-foot back"
				style={pose
					? `transform: translate(${pose.backFootPosition}) rotate(${pose.backFootRotation}deg)`
					: ''}
			></div>
			<div class="tee-body"></div>
			<div
				class="tee-foot front"
				style={pose
					? `transform: translate(${pose.frontFootPosition}) rotate(${pose.frontFootRotation}deg)`
					: ''}
			></div>
			<div
				class="tee-eyes"
				style={pose
					? `transform: translate(${pose.eyesPosition}) rotate(${pose.eyesRotation}deg)`
					: ''}
			>
				<div class="tee-eye-left"></div>
				<div class="tee-eye-right"></div>
			</div>
		</div>
	{/if}
</div>

